mapper.select:
SELECT * FROM ( SELECT LIST.*, ROWNUM AS RNUM FROM ( SELECT SCHEDULE.*, MOVIE.MOVIE_TITLE, MOVIE.MOVIE_TYPE, MOVIE.MOVIE_RELEASEDATE, MOVIE.MOVIE_SCREENTIME, MOVIE.MOVIE_STATUS, MOVIE.MOVIE_RATING, MOVIEROOM.BRANCH_NUMBER, MOVIEROOM.MOVIEROOM_NAME, MOVIEROOM.MOVIEROOM_2D, MOVIEROOM.MOVIEROOM_3D, MOVIEROOM.MOVIEROOM_4D, BRANCH.BRANCH_NAME FROM TBL_SCHEDULE SCHEDULE LEFT JOIN ( SELECT MOVIE_NUMBER, MOVIE_TITLE, MOVIE_TYPE, MOVIE_RELEASEDATE, MOVIE_SCREENTIME, MOVIE_STATUS, MOVIE_RATING FROM TBL_MOVIE ) MOVIE ON SCHEDULE.MOVIE_NUMBER = MOVIE.MOVIE_NUMBER LEFT JOIN ( SELECT MOVIEROOM_NUMBER, BRANCH_NUMBER, MOVIEROOM_NAME, MOVIEROOM_2D, MOVIEROOM_3D, MOVIEROOM_4D FROM TBL_MOVIEROOM ) MOVIEROOM ON SCHEDULE.MOVIEROOM_NUMBER = MOVIEROOM.MOVIEROOM_NUMBER LEFT JOIN ( SELECT BRANCH_NUMBER, BRANCH_NAME FROM TBL_BRANCH ) BRANCH ON BRANCH.BRANCH_NUMBER = MOVIEROOM.BRANCH_NUMBER WHERE MOVIE.MOVIE_STATUS = '상영중' AND SCHEDULE_START > (SYSDATE + 2/24)
AND BRANCH.BRANCH_NUMBER = #{branchNumber}
AND MOVIEROOM.MOVIEROOM_NUMBER = #{movieroomNumber}
AND MOVIE.MOVIE_NUMBER = #{movieNumber}
AND SCHEDULE.SCHEDULE_TYPE = #{scheduleType}
AND TO_DATE(SCHEDULE_START,'YYYY-MM-DD HH24:MI') > TO_DATE(#{scheduleStart},'YYYY-MM-DD HH24:MI')
AND TO_DATE(TRUNC(SCHEDULE_START, 'DD'),'YYYY-MM-DD') = TO_DATE(TRUNC(#{scheduleDate}, 'DD'),'YYYY-MM-DD')
ORDER BY SCHEDULE.SCHEDULE_START ASC, MOVIE.MOVIE_TITLE ASC ) LIST )

SELECT * FROM ( SELECT LIST.*, ROWNUM AS RNUM FROM ( SELECT DISTINCT BRANCH.BRANCH_NAME, BRANCH.BRANCH_NUMBER FROM TBL_SCHEDULE SCHEDULE LEFT JOIN ( SELECT MOVIE_NUMBER, MOVIE_TITLE, MOVIE_TYPE, MOVIE_RELEASEDATE, MOVIE_SCREENTIME, MOVIE_STATUS, MOVIE_RATING FROM TBL_MOVIE ) MOVIE ON SCHEDULE.MOVIE_NUMBER = MOVIE.MOVIE_NUMBER LEFT JOIN ( SELECT MOVIEROOM_NUMBER, BRANCH_NUMBER, MOVIEROOM_NAME, MOVIEROOM_2D, MOVIEROOM_3D, MOVIEROOM_4D FROM TBL_MOVIEROOM ) MOVIEROOM ON SCHEDULE.MOVIEROOM_NUMBER = MOVIEROOM.MOVIEROOM_NUMBER LEFT JOIN ( SELECT BRANCH_NUMBER, BRANCH_NAME FROM TBL_BRANCH ) BRANCH ON BRANCH.BRANCH_NUMBER = MOVIEROOM.BRANCH_NUMBER WHERE MOVIE.MOVIE_STATUS = '상영중' AND SCHEDULE_START > (SYSDATE + 2/24)
AND BRANCH.BRANCH_NUMBER = #{branchNumber}
AND MOVIEROOM.MOVIEROOM_NUMBER = #{movieroomNumber}
AND MOVIE.MOVIE_NUMBER = #{movieNumber}
AND SCHEDULE.SCHEDULE_TYPE = #{scheduleType}
AND TO_DATE(SCHEDULE_START,'YYYY-MM-DD HH24:MI') > TO_DATE(#{scheduleStart},'YYYY-MM-DD HH24:MI')
AND TO_DATE(TRUNC(SCHEDULE_START, 'DD'),'YYYY-MM-DD') = TO_DATE(TRUNC(#{scheduleDate}, 'DD'),'YYYY-MM-DD')
) LIST )

SELECT * FROM ( SELECT LIST.*, ROWNUM AS RNUM FROM ( SELECT DISTINCT MOVIE.* FROM TBL_SCHEDULE SCHEDULE LEFT JOIN ( SELECT MOVIE_NUMBER, MOVIE_TITLE, MOVIE_TYPE, MOVIE_RELEASEDATE, MOVIE_SCREENTIME, MOVIE_STATUS, MOVIE_RATING FROM TBL_MOVIE ) MOVIE ON SCHEDULE.MOVIE_NUMBER = MOVIE.MOVIE_NUMBER LEFT JOIN ( SELECT MOVIEROOM_NUMBER, BRANCH_NUMBER, MOVIEROOM_NAME, MOVIEROOM_2D, MOVIEROOM_3D, MOVIEROOM_4D FROM TBL_MOVIEROOM ) MOVIEROOM ON SCHEDULE.MOVIEROOM_NUMBER = MOVIEROOM.MOVIEROOM_NUMBER LEFT JOIN ( SELECT BRANCH_NUMBER, BRANCH_NAME FROM TBL_BRANCH ) BRANCH ON BRANCH.BRANCH_NUMBER = MOVIEROOM.BRANCH_NUMBER WHERE MOVIE.MOVIE_STATUS = '상영중' AND SCHEDULE_START > (SYSDATE + 2/24)
AND BRANCH.BRANCH_NUMBER = #{branchNumber}
AND MOVIEROOM.MOVIEROOM_NUMBER = #{movieroomNumber}
AND MOVIE.MOVIE_NUMBER = #{movieNumber}
AND SCHEDULE.SCHEDULE_TYPE = #{scheduleType}
AND TO_DATE(SCHEDULE_START,'YYYY-MM-DD HH24:MI') > TO_DATE(#{scheduleStart},'YYYY-MM-DD HH24:MI')
AND TO_DATE(TRUNC(SCHEDULE_START, 'DD'),'YYYY-MM-DD') = TO_DATE(TRUNC(#{scheduleDate}, 'DD'),'YYYY-MM-DD')
) LIST )
